import React, {useEffect, useState} from 'react';
import './App.css';
import Nav from './Nav';
import Header from './Header';
import Services from './Services';
import Menu from './Menu';
import Menus from './Menus';
import About from './About';
import TopPick from './TopPicks'
import News from './Newsletter';
import Footer from './Footer';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';

function App() {

  const scroll = scrollAppear => {
    let aboutText = document.querySelectorAll('.about-text');
    aboutText.forEach(text, function(){

    let introPosition = text.getBoundingClientRect().top;
    let screenPosition = window.innerHeight/1.3;

    if(introPosition < screenPosition){
      text.classList.add("about-appear");
  }
    })

}

window.addEventListener('scroll', scroll);

  return (
    <Router>
      <div className="App">
        <Nav/>
        <Switch>
          <Route path="/" exact component={Header}/>
          <Route path="/menu" exact component={Menu}/>
        </Switch>
        <Footer/>
      </div>
    </Router>
  );
}

export default App;
