import React, {useState, useEffect} from 'react';
import './App.css';
import TopPickList from './TopPickList'
import{Link} from 'react-router-dom';
import style from './sucre.module.css'

function TopPick() {
    const[products, setProducts] = useState([]);

    useEffect( ()=>{
      getProducts();
    }, []);

    const getProducts = async () =>{
        const url = "https://surce-delight.herokuapp.com/api/product/all/";
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        setProducts(data[0].product_variation);

    }

    let rows = [];

    for(let i = 0; i < 3; i++){
        products.map((product, i) => (
           rows.push(<TopPickList name = {product.product_type} price = {product.current_price}/>)
        ))
    }
    console.log(rows)

    return (
        <div className="top">
            <section className="top-picks">

            </section>
        </div>
    );
}

export default TopPick;
