function TopPicks() {
    const[products, setProducts] = useState([]);

    useEffect( ()=>{
      getProducts();
    }, []);

    const getProducts = async () =>{
          const url = "https://surce-delight.herokuapp.com/api/product/all/";
          const response = await fetch(url);
          const data = await response.json();
          console.log(data);
          setProducts(data[0].product_variation);

        }

  return (
    <div className="top">
{products.map(product => (
                <MenuList name = {product.product_type} price = {product.current_price} image = {product.product_image} desc = {product.description} key={product.id}/>
            ))
            }
    </div>
  );
}

export default TopPicks;
