import React from 'react';
import './App.css';
import{Link} from 'react-router-dom';
import style from './sucre.module.css'

function About() {
    const scroll = scrollAppear => {
        let aboutText = document.querySelector('.about-text');
        let introPosition = aboutText.getBoundingClientRect().top;
        let screenPosition = window.innerHeight;

        if(introPosition < screenPosition){
            aboutText.classList.add("about-appear");
        }
    }


  return (
    <div className="about">
        <section className="about-us">
            <div className="about-text">
                <h2>ABOUT</h2>
                <p>A bakery business founded in 2017 by Firdausy Bashir. we cater to your desert needs by delivering freshly baked goods and confectionaries to your doorstep.</p>
                <form>
                    <button className="btn-tertiary">Learn More</button>
                </form>
            </div>
            <div className="about-img">
                <img src="./Rectangle 3.jpg"/>
            </div>
        </section>
    </div>
  );
}

export default About;
