import React, {useEffect, useState} from 'react';
import{Link} from 'react-router-dom';
import './style.css'

function MenuList({name, itemDesc, itemQuantity, price, itemId}) {
    const[cart, setCart] = useState([]);
    const onSubmit = async () =>{
        const response = await fetch('https://surce-delight.herokuapp.com/api/cart/', {
            method: 'POST',
            headers: {
                'Accept': 'application/json'
            },
            body: data
        });
        const data = await response.json();
        console.log(data);
        setCart(data);

      }
    return (
        <div className="ourMenu-list">
            <Link className="link" to={`/menu/${itemId}`}>
                <div  className="Menu-box">
                    <div className="Menu-img">
                        <img src="./4C9E43EF-FD5D-4A3E-80E6-32C05E01B80C.jpeg" alt="small chop"/>
                    </div>
                    <div className="Menu-content">
                        <div className="Menu-text">
                            <h4>{name} </h4>
                            <p>{itemDesc}:{itemQuantity}</p>
                        </div>
                        <div className="Menu-price">
                            <h5>#{price}</h5>
                            <Link className="link" to={`/menu/${itemId}/cart`}>
                                <form onSubmit={onSubmit}>
                                    <a className="btn-link" href="">add to cart</a>
                                </form>
                            </Link>
                        </div>
                    </div>
                </div>
            </Link>
        </div>
    );
}

export default MenuList;
